sources.style.scss:
@use 'themeA';
@use 'themeB';

.container {
    border: 4px solid black;
    padding: 40px;
    font-family: themeB.$fuente-primaria;

    .box {
        border: 2px solid black;
        width: 20vw;
        height: 10vh;
        margin: 4px;
        padding: 10px;
        color: silver;
        border-radius: themeB.$radio-borde;       
    }

    .color-base {
        background-color: themeB.$color-base;
    }

    .color-important {
        background-color: themeB.$color-important;
    }

    .color-warning {
        background-color: themeB.$color-warning;
    }

    .fuente-secundaria {
        font-family: themeB.$fuente-secundaria;
    }

    .sombra {
        box-shadow: themeB.$sombra;
    }
}
